import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express, Request, Response } from "express";
import { createServer, type Server } from "http";
import bcrypt from "bcrypt";
import session from "express-session";
import { storage } from "./storage";
import { insertUserSchema, insertCourseSchema, insertJobSchema, insertPostSchema, insertMessageSchema } from "@shared/schema";
import { z } from "zod";

// Session middleware
declare module "express-session" {
  interface SessionData {
    userId?: string;
    userRole?: string;
  }
}

export async function registerRoutes(app: Express): Promise<Server> {
  app.use(
    session({
      secret: process.env.SESSION_SECRET || "skillconnect_secret_key",
      resave: false,
      saveUninitialized: false,
      cookie: { secure: false, maxAge: 24 * 60 * 60 * 1000 } // 24 hours
    })
  );

  // Auth middleware
  const requireAuth = (req: Request, res: Response, next: any) => {
    if (!req.session.userId) {
      return res.status(401).json({ message: "Authentication required" });
    }
    next();
  };

  // Auth routes
  app.post("/api/auth/register", async (req: Request, res: Response) => {
    try {
      const userData = insertUserSchema.parse(req.body);

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(userData.email);
      if (existingUser) {
        return res.status(400).json({ message: "User already exists" });
      }

      // Hash password
      const hashedPassword = await bcrypt.hash(userData.password, 12);

      const user = await storage.createUser({
        ...userData,
        password: hashedPassword
      });

      req.session.userId = user.id;
      req.session.userRole = user.role;

      res.json({ 
        user: { 
          id: user.id, 
          email: user.email, 
          username: user.username, 
          firstName: user.firstName,
          lastName: user.lastName,
          role: user.role 
        } 
      });
    } catch (error) {
      res.status(400).json({ message: "Invalid data", error });
    }
  });

  app.post("/api/auth/login", async (req: Request, res: Response) => {
    try {
      const { email, password } = req.body;

      const user = await storage.getUserByEmail(email);
      if (!user) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      const isValidPassword = await bcrypt.compare(password, user.password);
      if (!isValidPassword) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      req.session.userId = user.id;
      req.session.userRole = user.role;

      res.json({ 
        user: { 
          id: user.id, 
          email: user.email, 
          username: user.username, 
          firstName: user.firstName,
          lastName: user.lastName,
          role: user.role,
          profilePicture: user.profilePicture,
          walletBalance: user.walletBalance
        } 
      });
    } catch (error) {
      res.status(500).json({ message: "Login failed", error });
    }
  });

  app.post("/api/auth/logout", (req: Request, res: Response) => {
    req.session.destroy(() => {
      res.json({ message: "Logged out successfully" });
    });
  });

  app.get("/api/auth/me", async (req: Request, res: Response) => {
    if (!req.session.userId) {
      return res.status(401).json({ message: "Not authenticated" });
    }

    try {
      const user = await storage.getUser(req.session.userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      res.json({ 
        user: { 
          id: user.id, 
          email: user.email, 
          username: user.username, 
          firstName: user.firstName,
          lastName: user.lastName,
          role: user.role,
          profilePicture: user.profilePicture,
          walletBalance: user.walletBalance,
          bio: user.bio,
          skills: user.skills
        } 
      });
    } catch (error) {
      res.status(500).json({ message: "Failed to get user", error });
    }
  });

  // Course routes
  app.get("/api/courses", async (req: Request, res: Response) => {
    try {
      const { limit, category } = req.query;
      const courses = await storage.getCourses(
        limit ? parseInt(limit as string) : undefined,
        category as string
      );
      res.json(courses);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch courses", error });
    }
  });

  app.post("/api/courses", requireAuth, async (req: Request, res: Response) => {
    try {
      const courseData = insertCourseSchema.parse({
        ...req.body,
        instructorId: req.session.userId
      });

      const course = await storage.createCourse(courseData);
      res.json(course);
    } catch (error) {
      res.status(400).json({ message: "Failed to create course", error });
    }
  });

  app.get("/api/courses/:id", async (req: Request, res: Response) => {
    try {
      const course = await storage.getCourseById(req.params.id);
      if (!course) {
        return res.status(404).json({ message: "Course not found" });
      }
      res.json(course);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch course", error });
    }
  });

  app.post("/api/courses/:id/enroll", requireAuth, async (req: Request, res: Response) => {
    try {
      const enrollment = await storage.enrollInCourse(req.params.id, req.session.userId!);
      res.json(enrollment);
    } catch (error) {
      res.status(400).json({ message: "Failed to enroll in course", error });
    }
  });

  app.get("/api/my-courses", requireAuth, async (req: Request, res: Response) => {
    try {
      const enrolledCourses = await storage.getEnrolledCourses(req.session.userId!);
      res.json(enrolledCourses);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch enrolled courses", error });
    }
  });

  // Job routes
  app.get("/api/jobs", async (req: Request, res: Response) => {
    try {
      const { limit, type } = req.query;
      const jobs = await storage.getJobs(
        limit ? parseInt(limit as string) : undefined,
        type as string
      );
      res.json(jobs);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch jobs", error });
    }
  });

  app.post("/api/jobs", requireAuth, async (req: Request, res: Response) => {
    try {
      const jobData = insertJobSchema.parse({
        ...req.body,
        employerId: req.session.userId
      });

      const job = await storage.createJob(jobData);
      res.json(job);
    } catch (error) {
      res.status(400).json({ message: "Failed to create job", error });
    }
  });

  app.post("/api/jobs/:id/apply", requireAuth, async (req: Request, res: Response) => {
    try {
      const { coverLetter } = req.body;
      const application = await storage.applyToJob(req.params.id, req.session.userId!, coverLetter);
      res.json(application);
    } catch (error) {
      res.status(400).json({ message: "Failed to apply for job", error });
    }
  });

  app.get("/api/my-applications", requireAuth, async (req: Request, res: Response) => {
    try {
      const applications = await storage.getUserJobApplications(req.session.userId!);
      res.json(applications);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch applications", error });
    }
  });

  // Post routes
  app.get("/api/posts", async (req: Request, res: Response) => {
    try {
      const { limit } = req.query;
      const posts = await storage.getPosts(limit ? parseInt(limit as string) : undefined);
      res.json(posts);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch posts", error });
    }
  });

  app.post("/api/posts", requireAuth, async (req: Request, res: Response) => {
    try {
      const postData = insertPostSchema.parse({
        ...req.body,
        authorId: req.session.userId
      });

      const post = await storage.createPost(postData);
      res.json(post);
    } catch (error) {
      res.status(400).json({ message: "Failed to create post", error });
    }
  });

  app.post("/api/posts/:id/like", requireAuth, async (req: Request, res: Response) => {
    try {
      await storage.likePost(req.params.id, req.session.userId!);
      res.json({ message: "Post liked successfully" });
    } catch (error) {
      res.status(400).json({ message: "Failed to like post", error });
    }
  });

  // Message routes
  app.get("/api/messages/:userId", requireAuth, async (req: Request, res: Response) => {
    try {
      const messages = await storage.getMessages(req.session.userId!, req.params.userId);
      res.json(messages);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch messages", error });
    }
  });

  app.post("/api/messages", requireAuth, async (req: Request, res: Response) => {
    try {
      const messageData = insertMessageSchema.parse({
        ...req.body,
        senderId: req.session.userId
      });

      const message = await storage.sendMessage(messageData);
      res.json(message);
    } catch (error) {
      res.status(400).json({ message: "Failed to send message", error });
    }
  });

  app.get("/api/conversations", requireAuth, async (req: Request, res: Response) => {
    try {
      const conversations = await storage.getRecentConversations(req.session.userId!);
      res.json(conversations);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch conversations", error });
    }
  });

  // Search routes
  app.get("/api/search", async (req: Request, res: Response) => {
    try {
      const { q } = req.query;
      if (!q || typeof q !== 'string') {
        return res.status(400).json({ message: "Search query is required" });
      }

      const results = await storage.searchAll(q);
      res.json(results);
    } catch (error) {
      res.status(500).json({ message: "Search failed", error });
    }
  });

  // User profile routes
  app.get("/api/users/:id", async (req: Request, res: Response) => {
    try {
      const user = await storage.getUser(req.params.id);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Don't return sensitive information
      const { password, ...userWithoutPassword } = user;
      res.json(userWithoutPassword);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch user", error });
    }
  });

  app.patch("/api/users/me", requireAuth, async (req: Request, res: Response) => {
    try {
      const updates = req.body;
      // Don't allow updating sensitive fields
      delete updates.id;
      delete updates.password;
      delete updates.email;
      delete updates.walletBalance;

      const updatedUser = await storage.updateUser(req.session.userId!, updates);
      const { password, ...userWithoutPassword } = updatedUser;
      res.json(userWithoutPassword);
    } catch (error) {
      res.status(400).json({ message: "Failed to update user", error });
    }
  });

  // Dashboard stats
  app.get("/api/dashboard/stats", requireAuth, async (req: Request, res: Response) => {
    try {
      const userId = req.session.userId!;

      const [enrolledCourses, jobApplications, userPosts, transactions] = await Promise.all([
        storage.getEnrolledCourses(userId),
        storage.getUserJobApplications(userId),
        storage.getUserPosts(userId),
        storage.getUserTransactions(userId)
      ]);

      const completedCourses = enrolledCourses.filter(e => e.progress === 100);

      res.json({
        coursesEnrolled: enrolledCourses.length,
        certificatesEarned: completedCourses.length,
        jobApplications: jobApplications.length,
        connections: 0, // TODO: Implement connections count
        postsCreated: userPosts.length,
        totalTransactions: transactions.length
      });
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch dashboard stats", error });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, 
  courses, 
  jobs, 
  posts, 
  messages, 
  courseEnrollments, 
  jobApplications, 
  transactions,
  type User, 
  type InsertUser,
  type Course,
  type InsertCourse,
  type Job,
  type InsertJob,
  type Post,
  type InsertPost,
  type Message,
  type InsertMessage,
  type CourseEnrollment,
  type JobApplication,
  type Transaction
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, sql, ilike, or } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<User>): Promise<User>;

  // Course operations
  getCourses(limit?: number, category?: string): Promise<Course[]>;
  getCourseById(id: string): Promise<Course | undefined>;
  createCourse(course: InsertCourse): Promise<Course>;
  getUserCourses(userId: string): Promise<Course[]>;
  getEnrolledCourses(userId: string): Promise<(CourseEnrollment & { course: Course })[]>;
  enrollInCourse(courseId: string, studentId: string): Promise<CourseEnrollment>;

  // Job operations
  getJobs(limit?: number, type?: string): Promise<Job[]>;
  getJobById(id: string): Promise<Job | undefined>;
  createJob(job: InsertJob): Promise<Job>;
  applyToJob(jobId: string, applicantId: string, coverLetter?: string): Promise<JobApplication>;
  getUserJobApplications(userId: string): Promise<(JobApplication & { job: Job })[]>;

  // Post operations
  getPosts(limit?: number): Promise<(Post & { author: User })[]>;
  createPost(post: InsertPost): Promise<Post>;
  likePost(postId: string, userId: string): Promise<void>;
  getUserPosts(userId: string): Promise<Post[]>;

  // Message operations
  getMessages(userId: string, otherUserId: string): Promise<Message[]>;
  sendMessage(message: InsertMessage): Promise<Message>;
  getRecentConversations(userId: string): Promise<(Message & { sender: User, recipient: User })[]>;

  // Transaction operations
  createTransaction(transaction: Omit<Transaction, 'id' | 'createdAt'>): Promise<Transaction>;
  getUserTransactions(userId: string): Promise<Transaction[]>;

  // Search operations
  searchAll(query: string): Promise<{
    courses: Course[];
    jobs: Job[];
    users: User[];
    posts: (Post & { author: User })[];
  }>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async updateUser(id: string, updates: Partial<User>): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ ...updates, updatedAt: sql`now()` })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async getCourses(limit = 10, category?: string): Promise<Course[]> {
    const query = db.select().from(courses).orderBy(desc(courses.createdAt)).limit(limit);

    if (category) {
      return await query.where(eq(courses.category, category));
    }

    return await query;
  }

  async getCourseById(id: string): Promise<Course | undefined> {
    const [course] = await db.select().from(courses).where(eq(courses.id, id));
    return course || undefined;
  }

  async createCourse(insertCourse: InsertCourse): Promise<Course> {
    const [course] = await db
      .insert(courses)
      .values(insertCourse)
      .returning();
    return course;
  }

  async getUserCourses(userId: string): Promise<Course[]> {
    return await db.select().from(courses).where(eq(courses.instructorId, userId));
  }

  async getEnrolledCourses(userId: string): Promise<(CourseEnrollment & { course: Course })[]> {
    return await db
      .select()
      .from(courseEnrollments)
      .innerJoin(courses, eq(courseEnrollments.courseId, courses.id))
      .where(eq(courseEnrollments.studentId, userId));
  }

  async enrollInCourse(courseId: string, studentId: string): Promise<CourseEnrollment> {
    const [enrollment] = await db
      .insert(courseEnrollments)
      .values({ courseId, studentId })
      .returning();
    return enrollment;
  }

  async getJobs(limit = 10, type?: string): Promise<Job[]> {
    const query = db.select().from(jobs).where(eq(jobs.isActive, true)).orderBy(desc(jobs.createdAt)).limit(limit);

    if (type) {
      return await query.where(and(eq(jobs.isActive, true), eq(jobs.type, type as any)));
    }

    return await query;
  }

  async getJobById(id: string): Promise<Job | undefined> {
    const [job] = await db.select().from(jobs).where(eq(jobs.id, id));
    return job || undefined;
  }

  async createJob(insertJob: InsertJob): Promise<Job> {
    const [job] = await db
      .insert(jobs)
      .values(insertJob)
      .returning();
    return job;
  }

  async applyToJob(jobId: string, applicantId: string, coverLetter?: string): Promise<JobApplication> {
    const [application] = await db
      .insert(jobApplications)
      .values({ jobId, applicantId, coverLetter })
      .returning();
    return application;
  }

  async getUserJobApplications(userId: string): Promise<(JobApplication & { job: Job })[]> {
    return await db
      .select()
      .from(jobApplications)
      .innerJoin(jobs, eq(jobApplications.jobId, jobs.id))
      .where(eq(jobApplications.applicantId, userId));
  }

  async getPosts(limit = 20): Promise<(Post & { author: User })[]> {
    return await db
      .select()
      .from(posts)
      .innerJoin(users, eq(posts.authorId, users.id))
      .orderBy(desc(posts.createdAt))
      .limit(limit);
  }

  async createPost(insertPost: InsertPost): Promise<Post> {
    const [post] = await db
      .insert(posts)
      .values(insertPost)
      .returning();
    return post;
  }

  async likePost(postId: string, userId: string): Promise<void> {
    await db.transaction(async (tx) => {
      // Check if already liked
      const existing = await tx
        .select()
        .from(sql`post_likes`)
        .where(and(sql`post_id = ${postId}`, sql`user_id = ${userId}`));

      if (existing.length === 0) {
        // Add like
        await tx.insert(sql`post_likes`).values({ postId, userId });
        // Update post likes count
        await tx
          .update(posts)
          .set({ likesCount: sql`likes_count + 1` })
          .where(eq(posts.id, postId));
      }
    });
  }

  async getUserPosts(userId: string): Promise<Post[]> {
    return await db.select().from(posts).where(eq(posts.authorId, userId)).orderBy(desc(posts.createdAt));
  }

  async getMessages(userId: string, otherUserId: string): Promise<Message[]> {
    return await db
      .select()
      .from(messages)
      .where(
        or(
          and(eq(messages.senderId, userId), eq(messages.recipientId, otherUserId)),
          and(eq(messages.senderId, otherUserId), eq(messages.recipientId, userId))
        )
      )
      .orderBy(desc(messages.createdAt));
  }

  async sendMessage(insertMessage: InsertMessage): Promise<Message> {
    const [message] = await db
      .insert(messages)
      .values(insertMessage)
      .returning();
    return message;
  }

  async getRecentConversations(userId: string): Promise<(Message & { sender: User, recipient: User })[]> {
    return await db
      .select()
      .from(messages)
      .innerJoin(users, eq(messages.senderId, users.id))
      .innerJoin(sql`users as recipients`, eq(messages.recipientId, sql`recipients.id`))
      .where(or(eq(messages.senderId, userId), eq(messages.recipientId, userId)))
      .orderBy(desc(messages.createdAt))
      .limit(10);
  }

  async createTransaction(transaction: Omit<Transaction, 'id' | 'createdAt'>): Promise<Transaction> {
    const [newTransaction] = await db
      .insert(transactions)
      .values(transaction)
      .returning();
    return newTransaction;
  }

  async getUserTransactions(userId: string): Promise<Transaction[]> {
    return await db
      .select()
      .from(transactions)
      .where(eq(transactions.userId, userId))
      .orderBy(desc(transactions.createdAt));
  }

  async searchAll(query: string): Promise<{
    courses: Course[];
    jobs: Job[];
    users: User[];
    posts: (Post & { author: User })[];
  }> {
    const searchTerm = `%${query}%`;

    const [searchedCourses, searchedJobs, searchedUsers, searchedPosts] = await Promise.all([
      db.select().from(courses).where(or(ilike(courses.title, searchTerm), ilike(courses.description, searchTerm))).limit(5),
      db.select().from(jobs).where(or(ilike(jobs.title, searchTerm), ilike(jobs.description, searchTerm))).limit(5),
      db.select().from(users).where(or(ilike(users.username, searchTerm), ilike(users.firstName, searchTerm), ilike(users.lastName, searchTerm))).limit(5),
      db.select().from(posts).innerJoin(users, eq(posts.authorId, users.id)).where(ilike(posts.content, searchTerm)).limit(5)
    ]);

    return {
      courses: searchedCourses,
      jobs: searchedJobs,
      users: searchedUsers,
      posts: searchedPosts
    };
  }
}

export const storage = new DatabaseStorage();

