  FileText
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { useAuth } from "@/hooks/use-auth";

const navigation = [
  { name: "Dashboard", href: "/", icon: Home },
  { name: "My Learning", href: "/courses", icon: GraduationCap },
  { name: "Jobs", href: "/jobs", icon: Briefcase },
  { name: "Network", href: "/network", icon: Users },
  { name: "Feed", href: "/feed", icon: Newspaper },
  { name: "Messages", href: "/messages", icon: MessageCircle },
  { name: "Wallet", href: "/wallet", icon: Wallet },
];

const quickActions = [
  { name: "Create Course", icon: Plus },
  { name: "Post Job", icon: Briefcase },
  { name: "Create Post", icon: Edit },
];

export function Sidebar() {
  const [location] = useLocation();
  const { user } = useAuth();

  if (!user) return null;

  return (
    <aside className="hidden lg:block w-64 bg-white dark:bg-gray-800 border-r border-gray-200 dark:border-gray-700 min-h-screen">
      <div className="p-6">
        {/* User Profile Quick View */}
        <div className="mb-8">
          <div className="flex items-center mb-4">
            <img
              src={user.profilePicture || "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?ixlib=rb-4.0.3&auto=format&fit=crop&w=100&h=100"}
              alt="Profile"
              className="w-12 h-12 rounded-full"
            />
            <div className="ml-3">
              <p className="font-semibold">{user.firstName} {user.lastName}</p>
              <p className="text-sm text-gray-600 dark:text-gray-400 capitalize">{user.role}</p>
            </div>
          </div>
          <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-3">
            <div className="flex items-center justify-between text-sm">
              <span>Wallet Balance</span>
              <span className="font-semibold text-accent">${user.walletBalance || "0.00"}</span>
            </div>
          </div>
        </div>

        {/* Navigation Menu */}
        <nav className="space-y-2">
          {navigation.map((item) => {
            const isActive = location === item.href;
            const Icon = item.icon;

            return (
